import React, { useEffect } from "react";
import "./Contact.css";

const ContactForm = () => {
  useEffect(() => {
    const inputs = document.querySelectorAll(".input");

    function focusFunc() {
      let parent = this.parentNode;
      parent.classList.add("focus");
    }

    function blurFunc() {
      let parent = this.parentNode;
      if (this.value === "") {
        parent.classList.remove("focus");
      }
    }

    inputs.forEach((input) => {
      input.addEventListener("focus", focusFunc);
      input.addEventListener("blur", blurFunc);
    });

    // Cleanup event listeners on component unmount
    return () => {
      inputs.forEach((input) => {
        input.removeEventListener("focus", focusFunc);
        input.removeEventListener("blur", blurFunc);
      });
    };
  }, []);

  return (
    <div className="container">
      <span className="big-circle"></span>
      <img src="img/shape.png" className="square" alt="" />
      <div className="form">
        <div className="contact-info">
          <h3 className="title">Restons en contact</h3>
          <p className="text">
            Vous souhaitez échanger avec l’un de nos experts sur vos
            problématiques d’optimisation énergétique ? N’hésitez pas à nous
            contacter avec votre demande, nous reviendrons vers vous dans les
            plus brefs délais.
          </p>
        </div>

        <div className="contact-form">
          <span className="circle one"></span>
          <span className="circle two"></span>

          <form action="index.html" autoComplete="off">
            <h3 className="title">Contactez-nous</h3>
            <div className="input-container">
              <input type="text" name="name" className="input" />
              <label htmlFor="name">Nom d'utilisateur</label>
              <span>Nom d'utilisateur</span>
            </div>
            <div className="input-container">
              <input type="email" name="email" className="input" />
              <label htmlFor="email">Email</label>
              <span>Email</span>
            </div>
            <div className="input-container">
              <input type="tel" name="phone" className="input" />
              <label htmlFor="phone">Téléphone</label>
              <span>Téléphone</span>
            </div>
            <div className="input-container textarea">
              <textarea name="message" className="input"></textarea>
              <label htmlFor="message">Message</label>
              <span>Message</span>
            </div>
            <input type="submit" value="Envoyer" className="btn" />
          </form>
        </div>
      </div>
    </div>
  );
};

export default ContactForm;
