import React, { useEffect, useRef } from 'react';
import './Swiper.css'; // Make sure you have a CSS file for styles

const Swiper = () => {
  const sliderRef = useRef(null);

  useEffect(() => {
    const slider = sliderRef.current;

    const activate = (e) => {
      if (!slider) return;
      const items = slider.querySelectorAll('.item');
      if (e.target.matches('.next') && items.length > 0) {
        slider.appendChild(items[0]);
      }
      if (e.target.matches('.prev') && items.length > 0) {
        slider.prepend(items[items.length - 1]);
      }
    };

    document.addEventListener('click', activate);

    return () => {
      document.removeEventListener('click', activate);
    };
  }, []);

  return (
    <main>
      <ul ref={sliderRef} className="slider">
        <li className="item" style={{ backgroundImage: "url('https://cdn.mos.cms.futurecdn.net/dP3N4qnEZ4tCTCLq59iysd.jpg')" }}>
          <div className="content">
            <h2 className="title">"Lossless Youths"</h2>
            <p className="description">
              Lorem ipsum, dolor sit amet consectetur adipisicing elit. Tempore fuga voluptatum, iure corporis inventore praesentium nisi. Id laboriosam ipsam enim.
            </p>
            <button>Read More</button>
          </div>
        </li>
        {/* Add more items similarly */}
      </ul>
      <nav className="nav">
        <ion-icon className="btn prev" name="arrow-back-outline"></ion-icon>
        <ion-icon className="btn next" name="arrow-forward-outline"></ion-icon>
      </nav>

      <script type="module" src="https://unpkg.com/ionicons@7.1.0/dist/ionicons/ionicons.esm.js"></script>
      <script nomodule src="https://unpkg.com/ionicons@7.1.0/dist/ionicons/ionicons.js"></script>
    </main>
  );
};

export default Swiper;
