import React from 'react';
import { Swiper, SwiperSlide } from 'swiper/react';
import './Swiper.css'; // Assure-toi que le CSS de Swiper est importé
import { useRef, useEffect, useState } from 'react';

const SwiperComponent = () => {
    return (
      <div>
        <h1>Carousel</h1>
        <div style={{ height: '20px' }}></div>
  
        <Swiper
          spaceBetween={24} // Espace entre les slides
          slidesPerView={1} // Nombre de slides visibles
          navigation // Affiche les boutons de navigation
          pagination={{ clickable: true }} // Pagination cliquable
          onSlideChange={(swiper) => console.log('Slide index changed to:', swiper.activeIndex)} // Gestion du changement de slide
        >
          {[...Array(6).keys()].map(index => (
            <SwiperSlide key={index}>
              <div className="wiper-item">
                <img
                  className="wiper__image"
                  src={`https://images.pexels.com/photos/${index === 1 ? '17617625' : '19682307'}/pexels-photo-${index === 1 ? '17617625' : '19682307'}/free-photo-of-pink-chrysanthemum-flowers.jpeg`}
                  alt="background-image"
                />
                <button className="swiper__image-button">Click me</button>
              </div>
            </SwiperSlide>
          ))}
        </Swiper>
  
        <div style={{ height: '300px' }}></div>
      </div>
    );
  };
  
export default SwiperComponent;
