import React from 'react';
import { Swiper, SwiperSlide } from 'swiper/react';
import './Swiper.css'; // Assure-toi que le CSS de Swiper est importé


const SwiperComponent = () => {
    const wiperTrackRef = useRef(null);
    const wipesRef = useRef([]);
    const wipePrevBtnRef = useRef(null);
    const wipeNextBtnRef = useRef(null);
    const [activeIndex, setActiveIndex] = useState(0);
  
    useEffect(() => {
      const wiperTrack = wiperTrackRef.current;
      const wipes = wipesRef.current;
      const wipePrevBtn = wipePrevBtnRef.current;
      const wipeNextBtn = wipeNextBtnRef.current;
      const wipeWidth = wipes[0]?.getBoundingClientRect().width;
  
      const arrowsBehaviour = (index) => {
        if (index === 0) {
          wipePrevBtn.classList.add("is-hidden");
          wipeNextBtn.classList.remove("is-hidden");
        } else if (index === wipes.length - 1) {
          wipePrevBtn.classList.remove("is-hidden");
          wipeNextBtn.classList.add("is-hidden");
        } else {
          wipePrevBtn.classList.remove("is-hidden");
          wipeNextBtn.classList.remove("is-hidden");
        }
      };
  
      const wipeSlide = (targetIndex) => {
        wiperTrack.style.transform =
          "translateX(-" + (wipeWidth + 24) * targetIndex + "px)";
        const activeSlide = wiperTrack.querySelector(".active-swipe");
        const nextSlide = wipes[targetIndex];
        if (activeSlide) {
          activeSlide.classList.remove("active-swipe");
          activeSlide.style.transform = "scale(1)";
        }
        nextSlide.classList.add("active-swipe");
        nextSlide.style.transform = "scale(1.1)";
      };
  
      const handleNextClick = () => {
        if (activeIndex < wipes.length - 1) {
          const nextIndex = activeIndex + 1;
          wipeSlide(nextIndex);
          setActiveIndex(nextIndex);
          arrowsBehaviour(nextIndex);
        }
      };
  
      const handlePrevClick = () => {
        if (activeIndex > 0) {
          const prevIndex = activeIndex - 1;
          wipeSlide(prevIndex);
          setActiveIndex(prevIndex);
          arrowsBehaviour(prevIndex);
        }
      };
  
      wipeNextBtn.addEventListener("click", handleNextClick);
      wipePrevBtn.addEventListener("click", handlePrevClick);
      return () => {
        wipeNextBtn.removeEventListener("click", handleNextClick);
        wipePrevBtn.removeEventListener("click", handlePrevClick);
      };
    }, [activeIndex]);
  
    return (
      <div>
        <h1>Carousel</h1>
        <div style={{ height: '20px' }}></div>
  
        <div className="wiper">
          <button className="wiper-button wiper-button__right" ref={wipeNextBtnRef}>
            <img src="https://www.iconpacks.net/icons/2/free-arrow-left-icon-3099-thumb.png" alt="left" />
          </button>
          <div className="wiper-wrapper">
            <ul className="wiper-track" ref={wiperTrackRef}>
              {[...Array(6).keys()].map(index => (
                <li
                  key={index}
                  className={`wiper-item ${index === 1 ? 'active-swipe' : ''}`}
                  ref={el => wipesRef.current[index] = el}
                >
                  <img
                    className="wiper__image"
                    src={`https://images.pexels.com/photos/${index === 1 ? '17617625' : '19682307'}/pexels-photo-${index === 1 ? '17617625' : '19682307'}/free-photo-of-pink-chrysanthemum-flowers.jpeg`}
                    alt="background-image"
                  />
                  <button className="swiper__image-button">Click me</button>
                </li>
              ))}
            </ul>
          </div>
          <button className="wiper-button wiper-button__left" ref={wipePrevBtnRef}>
            <img src="https://www.iconpacks.net/icons/2/free-arrow-left-icon-3099-thumb.png" alt="right" />
          </button>
        </div>
        <div style={{ height: '300px' }}></div>
      </div>
    );
  };
  
  
  
export default SwiperComponent;
