import React, { useState } from 'react';
import { useNavigate, Link } from 'react-router-dom';

const Login = () => {
  const [formData,setFormData] = useState({
    username :'',
    email:'',
    password:''
  }) 

  const changeHandler = (e) => {
    setFormData({...formData ,[e.target.name] : e.target.value })
  }
  const navigate = useNavigate();

  const handleSubmit = async (e) => {
    e.preventDefault();
    // Remplace cette logique par celle qui vérifie si le compte existe réellement
    const userExists = await checkUser(email, password); 

    if (userExists) {
      // Logique de connexion réussie
      navigate('/dashboard'); // Redirection vers une page protégée après connexion
    } else {
      setError('Email ou mot de passe incorrect. Si vous n\'avez pas de compte, vous pouvez vous inscrire.');
    }
  };

  const checkUser = async (email, password) => {
    // Ici, tu devrais faire une requête API pour vérifier les informations de connexion
    // Remplace cette logique par une vraie vérification d'utilisateur
    return false; // Simule que l'utilisateur n'existe pas
  };

  return (
    
    <div className="login-container">
      <h2>Login</h2>
      <form onSubmit={handleSubmit}>
        <div className="form-group">
          <label htmlFor="email">Email</label>
          <input
            type="email"
            id="email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            required
          />
        </div>
        <div className="form-group">
          <label htmlFor="password">Password</label>
          <input
            type="password"
            id="password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            required
          />
        </div>
        <button type="submit">Login</button>
        {error && <p className="error">{error}</p>}
      </form>
      <p>Don't have an account? <Link to="/signup">Sign up</Link></p>
    </div>
  );
};

export default Login;
